function createCookiePolicyCookie() {
	var name = "show-cookie-policy-linzag";
	var value = false;
	var days = 1;
	var expires = "";
	if (days) {
		var date = new Date();
		date.setTime(date.getTime() + (days*24*60*60*1000));
		expires = "; expires=" + date.toUTCString();
	}
	document.cookie = name + "=" + value + expires + ";domain=.linzag.at; path=/";
}

