		//
		function splitDate() {
			if (document.fahrplanabfrage.selectedDate.value!=null) {
				day 	= document.fahrplanabfrage.selectedDate.value.substring(0,2);
				month 	= document.fahrplanabfrage.selectedDate.value.substring(3,5);;
				year 	= document.fahrplanabfrage.selectedDate.value.substring(6,10);
				document.fahrplanabfrage.itdDate.value=year+month+day;
			}
		}
		function parseTime(timeString) {    
			if (timeString == '') {
 				return null;
			}
			if (timeString.length == 3) {
				timeString = "0" + timeString;
			}
			if (timeString.length == 4) {
				timeString = timeString.substr(0, 2) + ":" + timeString.substr(2);			
			}
			var time = timeString.match(/(\d+)(:(\d\d))?\s*(p?)/i); 
			if (time == null) {
 				return null;
			}
			var hours = parseInt(time[1],10);
			if (hours == 24 && !time[4]) {
      			hours = 0;
			} else {
    			hours += (hours < 24 && time[4])? 24 : 0;
			}   
			var d = new Date();             
			d.setHours(hours);
			d.setMinutes(parseInt(time[3],10) || 0);
			d.setSeconds(0, 0);  
			return d;
		}
		function splitTime() {
			var timeString = document.fahrplanabfrage.selectedTime.value; 
			d = parseTime(timeString);
			var hours = d.getHours().toString();
			if (hours.length == 1) {
				hours = "0" + hours;
			}
			var minutes = d.getMinutes().toString();
			if (minutes.length == 1) {
				minutes = "0" + minutes;
			}
			if (minutes.length != 2) {
				minutes = "00";
			}
			if (document.fahrplanabfrage.selectedTime.value!=null) {
				hour = hours;
				minute = minutes;
				if (hour == null) {
					hour = "00";
				}
				if ((minute == null)||(minute == '')) {
					minute = "00";
				}
				document.fahrplanabfrage.itdTime.value = hour+minute;
			}
		}
