window.addEventListener("load", function(){
window.cookieconsent.initialise({
  "palette": {
    "popup": {
      "background": "#000"
    },
    "button": {
      "background": "#f1d600"
    }
  },
  "content": {
    "message": "Diese Website benutzt Cookies, um Ihre Benutzererfahrung zu verbessern.",
    "dismiss": "Ok",
    "link": "Mehr erfahren"
  }
})});

  $( document ).ready(function() {
    $('.topMenuMobileHolder .menuButtons a.showMenu').click(function() {
      $('.topMenuMobileHolder ul#topmenuMobile').slideToggle(300);
      return false;
    })

    $('.topMenuMobileHolder .menuButtons a.showSearch').click(function() {
      $('.topMenuMobileHolder div#topsearch').slideToggle(300);
      return false;
    })

    $('.topMenuMobileHolder .menuButtons a.showNewsletter').click(function() {
      $('.topMenuMobileHolder div.newsletter').slideToggle(300);
      return false;
    })

    if ($('.submenuMobile ul li.menuItemActive').length > 0) {
      addSubMenuMobileHeader();
    }

    function addSubMenuMobileHeader() {
      //console.log("added");
      var activeMenuText = $('.submenuMobile ul li.menuItemActive a').text();
      var headerHtml = '
'+activeMenuText+'';
      $('.submenuMobile').prepend(headerHtml);
      $('.submenuMobile h2.submenuHeader').click(function() {
      $('.submenuMobile .news-catmenu').slideToggle(300);
      });
    }

    var ratio = 1;
    var orgWidth;
    var orgHeight;

    function resizeVideo() {
      if ($('.band_youtube iframe').length > 0 && $(window).width() < 550) {
        var newWidth = $('.band_youtube').width();
        var newHeight = newWidth / ratio;
        $('.band_youtube iframe').attr({width: newWidth, height: newHeight}).css({width: newWidth, height: newHeight});
      } else if ($('.band_youtube iframe').length > 0) {
        $('.band_youtube iframe').attr({width: orgWidth, height: orgHeight}).css({width: orgWidth, height: orgHeight});
      }
    }

    if ($('.band_youtube embed').length > 0) {
      orgWidth = $('.band_youtube iframe').attr("width");
      orgHeight = $('.band_youtube iframe').attr("height");
      var ratio = $('.band_youtube iframe').attr("width") / $('.band_youtube embed').attr("height");
      //console.log("wid:"+orgWidth+", heig:"+orgHeight+", ratio:"+ratio);
      resizeVideo();
    }

    $(window).resize(function() {
      resizeVideo();
    });




  });

