    var ratio = 1;
    var orgWidth;
    var orgHeight;

    function resizeVideo() {
      if ($('.band_youtube iframe').length > 0 && $(window).width() < 550) {
        var newWidth = $('.band_youtube').width();
        var newHeight = newWidth / ratio;
        $('.band_youtube iframe').attr({width: newWidth, height: newHeight}).css({width: newWidth, height: newHeight});
      } else if ($('.band_youtube iframe').length > 0) {
        $('.band_youtube iframe').attr({width: orgWidth, height: orgHeight}).css({width: orgWidth, height: orgHeight});
      }
    }

    if ($('.band_youtube embed').length > 0) {
      orgWidth = $('.band_youtube iframe').attr("width");
      orgHeight = $('.band_youtube iframe').attr("height");
      var ratio = $('.band_youtube iframe').attr("width") / $('.band_youtube embed').attr("height");
      //console.log("wid:"+orgWidth+", heig:"+orgHeight+", ratio:"+ratio);
      resizeVideo();
    }

    $(window).resize(function() {
      resizeVideo();
    });




