var userDataJSON = null;
var userData = null;

$(document).ready(function() {
	$('#userdataelements').hide();
	$('#loginelements').hide();

	$.ajaxSetup({
		cache:false
	});

	//userDataJSON = $.getJSON('/login/?action=json', function(data) {

	$.ajax({
		url: '/login/?action=json',
		dataType: 'json',
		type: 'get',
		complete: ajaxSucceed,
		cache: false
	});
	//});
});

function ajaxSucceed(response) {
	data = response['responseText'];
	userData = jQuery.parseJSON(data);

	if(userData != null && userData.sessionExists) {
		if (userData.username != null) {
			$('#welcomeBoxOld').hide();
			$('#welcomeBoxLoggedinUser').show();
			$('#welcomeNameLoggedinUser').text(userData.username);
		}
		//alert("Username " + userData.username);
		//alert("ProductIDS " + userData.productIds);
		//alert("extended "+ userData.extended);
		//alert("loggedInWithPW " +userData.loggedInWithPW);
	  	var username = data.username;
	  	if(userData.displayname && jQuery.trim(userData.displayname).length > 0) {
	  		username = userData.displayname;
	  	}
		  	$('#usernamefield').html(username);
			$('#userdataelements').show();
			$('li.linkFooterTermine').show(); // Footer fur alle angemeldeten User
			if (userData.loggedInWithPW) {
			 $('#linklogout').show();
			}
			if (userData.extended) { // Gruppe B
				$('.tomemberarealink').show();
				$('#welcome_text_group_b').show();
				$('#linklogout').show();
			} else {
			  	$('#welcome_text_group_a').show(); // Gruppe A
			  	$('#linklogout').show();
			}

		} else { // nicht eingeloggt
		  $('#welcomeBoxOld').show();
			$('#loginelements').show();
		}

	   if(typeof setUserDataComments == 'function') {
	   	setUserDataComments();
	   }
	}

function setCookie(cname, cvalue, exdays) {
    var d = new Date();
    d.setTime(d.getTime() + (exdays*24*60*60*1000));
    var expires = "expires="+d.toUTCString();
    var path = "path=/";
    document.cookie = cname + "=" + cvalue + "; " + expires + "; " + path;
}

function getCookie(cname) {
    var name = cname + "=";
    var ca = document.cookie.split(';');
    for(var i=0; i<ca.length; i++) {
        var c = ca[i];
        while (c.charAt(0)==' ') c = c.substring(1);
        if (c.indexOf(name) == 0) return c.substring(name.length, c.length);
    }
    return "";
}

